// index.js

import React, { useState } from 'react';
import QRCode from 'qrcode.react';
import styles from "./styles.module.css";

const Main = () => {
    const [username, setUsername] = useState('');
    const [qrType, setQrType] = useState('default'); // To store selected QR type

    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };

    const downloadQRCode = () => {
        const canvas = document.getElementById("qr-code");
        const pngUrl = canvas.toDataURL("image/png").replace("image/png", "image/octet-stream");

        const downloadLink = document.createElement("a");
        downloadLink.href = pngUrl;
        downloadLink.download = "qr-code.png";
        document.body.appendChild(downloadLink);
        downloadLink.click();
        document.body.removeChild(downloadLink);
    };

    const handleChangeQRType = (e) => {
        setQrType(e.target.value);
    };

    return (
        <div className={styles.main_container}>
            <nav className={styles.navbar}>
                <h1>fakebook</h1>
                <button className={styles.white_btn} onClick={handleLogout}>
                    Logout
                </button>
            </nav>
            <div>
                <h2>Generate QR Code</h2>
                <input 
                    type="text" 
                    placeholder="Enter your username" 
                    value={username} 
                    onChange={(e) => setUsername(e.target.value)}
                />
                <select value={qrType} onChange={handleChangeQRType}>
                    <option value="default">Default</option>
                    <option value="watermark">With Watermark</option>
                    <option value="colorful">Colorful</option>
                    <option value="shapes">With Shapes</option>
                    <option value="custom">Custom</option>
                </select>
                <QRCode id="qr-code" value={username} options={{ 
                    ...(qrType === 'watermark' && { logo: '/path/to/watermark.png' }), 
                    ...(qrType === 'colorful' && { fgColor: '#FF0000' }), 
                    ...(qrType === 'shapes' && { renderAs: 'svg' }),
                    ...(qrType === 'custom' && { /* Add custom options here */ }),
                }} />
                <button onClick={downloadQRCode}>Download QR Code</button>
            </div>
        </div>
    );
};

export default Main;
